DECLARE @@DIVISION_ID VARCHAR(5)='';
SELECT @@DIVISION_ID = (SELECT TOP 1 DIVISION_ID FROM TB_R_SYNCH_EMPLOYEE  WHERE GETDATE() BETWEEN VALID_FROM AND VALID_TO AND NOREG=@RegNo)
IF (@@DIVISION_ID='14')
BEGIN
	SELECT CASE WHEN @@DIVISION_ID='14' AND COALESCE(@PlantCd,'') = '0000'
		   THEN 'FALSE'
		   ELSE 'TRUE'
	END AS SetDisablePlant
END
ELSE
BEGIN
	SELECT 'FALSE' AS SetDisablePlant
END
